    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Window window = SwingUtilities.getWindowAncestor(HotelForm.this);

        String nombre = nombreTxt.getText();
        String pais = paisTxt.getText();
        String localizacion = localizacionTxt.getText();
        String categoria = categoriaComboBox.getSelectedItem().toString();

        hotel.setNombre(nombre);
        hotel.setPais(pais);
        hotel.setLocalizacion(localizacion);
        hotel.setCategoria(categoria);

        window.dispose();
    }//GEN-LAST:event_jButton1ActionPerformed


